from typing import Optional

from baseopensdk.core.const import FEISHU_DOMAIN
from baseopensdk.core import LogLevel


class Config(object):
    def __init__(self) -> None:
        self.domain: str = FEISHU_DOMAIN  # 域名
        self.log_level: LogLevel = LogLevel.INFO
        self.app_token: Optional[str] = None  # Base appToken
        self.personal_base_token: Optional[str] = None  # Base 鉴权 token
        self.timeout: Optional[float] = None  # 客户端超时时间, 单位秒, 默认永不超时